commits.content:
:root {
    --dark-grey: #757575; 
    --purple:#a445ed; 
    --light-grey:#f4f4f4; 
    --dark-three:#2d2d2d; 
    --lighter-grey:#e9e9e9; 
}


* {
    margin: 0;
    padding: 0;
}



/* Main Styling */
body {
    max-width: 1100px;
    margin: 0 auto;
    font-family: sans-serif;
    color: var(--dark-three);
}


/* Light Styling */

/* Nav Styling */
nav {
    display: flex;
    justify-content: space-around;
    margin-top: 3rem;
}

.right {
    display: flex;
    gap: 2rem;
}

.fonts {
    display: flex;
    gap: 1rem;
    align-items: center;
    font-weight: 600;
}

.arrow-down {

}

.ruler {
    width: 1px;
    background-color: #e9e9e9;
}

.togglers {
    display: flex;
    gap: 2rem;
    align-items: center;
}



/* The switch - the box around the slider */
.switch {
  position: relative;
  display: inline-block;
  width: 40px;
  height: 20px;
}

/* Hide default HTML checkbox */
.switch input {
  opacity: 0;
  width: 0;
  height: 0;
}

/* The slider */
.slider {
  position: absolute;
  cursor: pointer;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: var(--dark-grey);
  -webkit-transition: .4s;
  transition: .4s;
}

.slider:before {
  position: absolute;
  content: "";
  height: 14px;
  width: 14px;
  left: 3px;
  bottom: 3px;
  background-color: white;
  -webkit-transition: .4s;
  transition: .4s;
}

input:checked + .slider {
  background-color: var(--purple)
}

input:focus + .slider {
  box-shadow: 0 0 1px var(--purple)
}

input:checked + .slider:before {
  -webkit-transform: translateX(20px);
  -ms-transform: translateX(20px);
  transform: translateX(20px);
}

/* Rounded sliders */
.slider.round {
  border-radius: 34px;
}

.slider.round:before {
  border-radius: 50%;
}



/* Search */

.search {
    text-align: center;
    margin-top: 4rem;
    display: flex;
    justify-content: center;
}

input[type="text"] {
    width:60%; 
    height: 1.5rem;
    padding: 1rem;
    border-radius: 16px;
    border: none;
    background-color: var(--light-grey);
    outline: none;
    font-size: 20px;
    font-weight: 700; 

}

input[type="text"]:active, 
input[type="text"]:focus {
    border: 1px solid var(--purple);
}


input[type="text"]::placeholder {
    opacity: 0.25;
}

.search-icon {
    transform: translate(-50px,1px);
    text-align: center;
}



/* Word View */

.word-view {
    text-align: center;
    margin-top: 4rem;
    display: flex;
    justify-content: space-around;
}
.word-group h1 {
    font-size: 4rem;
}

.word-group p {
    text-align: left;
    font-size: 1.5rem;
    color: var(--purple);
    font-weight: 400;
    padding-top: 0.5rem;
    letter-spacing: 2px;
}


/* Speech Part(noun, pronoun, verb) */
.speech-part-group {
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 20px;
    margin-top: 3rem;
}
.speech-part {
    text-align: left;
    font-size: 2rem;
    font-weight: 700;
    font-style: italic;
}
.grey-rule {
    border: 1px solid var(--lighter-grey);
    height: 0;
    width: 56%;
}

/* Description */
.description {
    /* width: fit-content;
    margin: 0 auto;
    margin-top: 2rem; */
    width: 65%;
    /* margin-left: 17.5%; */
    margin: 0 auto;
}


.description p {
    color: var(--dark-grey);
    text-align: left;
    font-size: 1.25rem;
    padding: 20px 1px;

}

ul {
    text-align: left;
    padding: 10px 20px;
    list-style: none;
}
ul li::before {
    content: "\2022";
    width: 1rem;
    font-weight: bold;
    display: inline-block;
    margin-left: -1em;
    color: var(--purple);
}
ul li{
    padding: 5px;
    color: var(--dark-three);
}
.synonymn span {
    color: var(--purple);
    padding: 0 20px;
}


/* Dynamic Styles */






/* Mobile Styels */

@media screen and (max-width:375px) {

    input[type="text"] {
        width: 327px;
    }
    .search {
        margin-left: 10px;
    }

    .word-group h1 {
        font-size: 32px;
    }

    .icon-play {
        width: 48px;
    }

    .description {
        width: 275px;
    }

    .speech-part {
        font-size: 18px;
    }

    .description p {
        font-size: 16px;
    }
}

@media screen and (min-width:580px) and (max-width:768px) {
    nav {
        justify-content: space-between;
        padding: 20px;
    }
    input[type="text"] {
        width: 689px;
    }

    .word-view {
        justify-content: space-between;
        padding: 20px;
    }

    .speech-part-group {
        justify-content: space-between;
        padding: 20px;
    }

    .speech-part {
        text-align: left;
        font-size: 24px;
    }
    .grey-rule {
        border: 1px solid var(--lighter-grey);
        height: 0;
        width: 608px;
    }
    .description {
        text-align: left;
        margin: 0;
        width: 500px;
        padding: 20px;
    }


}
